import React, {useEffect, useState} from 'react';
import { List, Avatar, Space } from 'antd';
import { Line } from "react-chartjs-2"

const Artist = (props) => {
    const [chartData, setChartData] = useState({});
    console.log(props);
    const chart = () => {
        const albumScores = [];
        const albumNames = [];
        for (const dataObj of props.data) {
            albumScores.push(dataObj.sentimentVal);
            console.log(dataObj.sentimentVal);
            albumNames.push(dataObj.name);
            console.log(dataObj.name);
        }
        setChartData({
            labels: albumNames,
            datasets: [
                {
                    label: "Sentiment over Albums",
                    data: albumScores,
                    backgroundColor: ["rgba(75, 192, 192, 0.6)"],
                    borderWidth: 4
                }
            ]
        });
        console.log(chartData);
        console.log(albumNames, albumScores);
    };

    useEffect(() => {
        console.log("useEffect called")
        console.log(props);
        chart();
    }, [props]);

    return (
        <div>
        <Line
            data={chartData}
            options={{
                responsive: true,
                title: { text: "Sentiment", display: true },
                scales: {
                    yAxes: [
                        {
                            ticks: {
                                autoSkip: true,
                                maxTicksLimit: 10,
                                beginAtZero: true
                            },
                            gridLines: {
                                display: true
                            }
                        }
                    ],
                    xAxes: [
                        {
                            gridLines: {
                                display: true
                            }
                        }
                    ]
                }
            }}
        />
        <List
            itemLayout="vertical"
            size="large"
            pagination={{
                onChange: page => {
                    console.log(page);
                },
                pageSize: 3,
            }}
            dataSource={props.data}
            // footer={ }
            renderItem={item => (
                <List.Item
                    key={item.name}
                    actions={[]}
                    extra={
                        <img
                            width={272}
                            alt="logo"
                            src={item.picture}
                        />
                    }
                >
                    <List.Item.Meta
                        // avatar={<Avatar src={item.avatar} />}
                        title={<a href={`/artist/${item.id}`}>{item.name}</a>}
                        description={item.sentimentVal}
                    />
                    {item.content}
                </List.Item>
            )}
        />
        </div>
    )
}

export default Artist;
